package loops;

public class HelloWorld {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		for (int i = 1; i <= 1; i++) {
			for (int j = 1; j <= 4; j++) {
				System.out.println((i) + "     " + (i + 1) + "     " + (j) + "     " + (i + 3) + "     "
						+ (i + 4));
			}
		}
	}

}
