package loops;

public class NumberOutput {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		for (int i = 1; i <= 1; i++) {
			for (int j = 1; j <= 5; j++) {
				System.out.println((j) + "     " + (j + 1) + "     " + (j + 2) + "     " + (j + 3) + "     " + (i + 4)
						+ "     " + (j));
			}
		}
	}

}
